// components/RotatingIcons.js
'use client';
import { useEffect, useRef } from 'react';
import * as THREE from 'three';

const RotatingIcons = () => {
  const containerRef = useRef(null);

  useEffect(() => {
    const container = containerRef.current;

    // Create Scene, Camera, Renderer
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, container.offsetWidth / container.offsetHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true }); // Add alpha: true for transparency
    renderer.setSize(container.offsetWidth, container.offsetHeight);
    renderer.setClearColor(0x000000, 0); // Transparent background
    container.appendChild(renderer.domElement);

    // Add Icons as Sprites
    const textureLoader = new THREE.TextureLoader();
    const icons = [
      { url: 'https://cdn-icons-png.flaticon.com/512/5968/5968231.png', position: { x: 3, y: 2, z: 1 } },
      { url: 'https://cdn-icons-png.freepik.com/256/721/721671.png', position: { x: -3, y: -2, z: 1 } },
      { url: 'https://git-scm.com/images/logos/downloads/Git-Icon-Black.png', position: { x: 2, y: 3, z: -2 } },
      { url: 'data:image/png;base64,', position: { x: -2, y: -3, z: 2 } },
      { url: 'https://brandslogos.com/wp-content/uploads/thumbs/react-logo-black-and-white-1.png', position: { x: 0, y: 4, z: 3 } },
      { url: 'https://www.domeniclabbate.com/_next/static/media/express.27b48634.png', position: { x: 3, y: 1, z: -3 } },
      { url: 'https://upload.wikimedia.org/wikipedia/commons/4/4b/Tailwind_CSS_Logo.svg', position: { x: 2, y: -4, z: 2 } },
      { url: 'data:image/png;base64,', position: { x: 2, y: 4, z: -1 } },
      { url: 'data:image/png;base64,', position: { x: 2, y: -4, z: 2 } },
      { url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRWfIhaWqloiY2dwb5-DDs1B5yziC7WKHhnkg&s', position: { x: -4, y: 0, z: -2 } },
      { url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSmLMV3oOWMSoJtmKHAv5arDfxpZ25VU6MOBdKZqWyx4Wl3QwubpOWOH45e1NL_bknB0e0&usqp=CAU', position: { x: 4, y: -2, z: -3 } },
      { url: 'data:image/png;base64,', position: { x: -4, y: 3, z: 1 } },
      { url: 'data:image/jpeg;base64,', position: { x: 1, y: -3, z: -4 } },
      { url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQsMjGv1FnFCZh_0mayzMeAu0f-LbicOoufA&s', position: { x: 4, y: 2, z: -3 } },
      { url: 'data:image/png;base64,', position: { x: -4, y: -2, z: 2 } },
    
    ];

    const sprites = [];
    icons.forEach(icon => {
      const spriteMaterial = new THREE.SpriteMaterial({ map: textureLoader.load(icon.url) });
      const sprite = new THREE.Sprite(spriteMaterial);
      sprite.position.set(icon.position.x, icon.position.y, icon.position.z);
      sprite.scale.set(1.5, 1.5, 1.5); // Set size of icons
      scene.add(sprite);
      sprites.push(sprite);
    });

    camera.position.z = 12;

    // Animate Icons
    function animate() {
      requestAnimationFrame(animate);

      // Rotate each icon around a common point
      sprites.forEach(sprite => {
        const { x, y, z } = sprite.position;
        sprite.position.x = x * Math.cos(0.01) - z * Math.sin(0.01);
        sprite.position.z = z * Math.cos(0.01) + x * Math.sin(0.01);
        sprite.position.y = y * Math.cos(0.005) - z * Math.sin(0.005);
      });

      renderer.render(scene, camera);
    }
    animate();

    // Resize Handling
    const handleResize = () => {
      camera.aspect = container.offsetWidth / container.offsetHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(container.offsetWidth, container.offsetHeight);
    };
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      container.removeChild(renderer.domElement);
    };
  }, []);

  return <div ref={containerRef} style={{ width: '100%', height: '100vh' }} />;
};

export default RotatingIcons;
